layout(set = 2, binding = 11) uniform sampler2D cc_spriteTexture;
layout(location = 0) in vec4 color;
layout(location = 1) in vec2 uv0;
layout(set = 1, binding = 0) uniform Constant {
  vec4 mainColor;
};
vec4 frag () {
  vec4 o = vec4(1, 1, 1, 1);
